import { REG_MID_SUC, REG_FAILD, REG_SUC } from './types';
import axios from 'axios';

export const regM = ({
  name,
  email,
  password,
  adress,
  phonNumber
} = async dispatch => {
  const config = {
    header: {
      'Content-Type': 'application/json'
    }
  };

  const body = JSON.stringify({ name, email, password, adress, phonNumber });
  try {
    const res = await axios.post('/api/users', body, config);
    dispatch({
      type: REG_MID_SUC,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: REG_FAILD,
      payload: res.data
    });
  }
});

export const regS = (mailToken = async dispatch => {
  const config = {
    header: {
      'Content-Type': 'application/json'
    }
  };

  const body = JSON.stringify(mailToken);
  try {
    const res = await axios.post(`/api/users/${mailToken}`, body, config);
    dispatch({
      type: REG_SUC,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: REG_FAILD,
      payload: res.data
    });
  }
});
