import React, { useState } from 'react';
import { loginUser } from '../../action/auth';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import './css/style.css';

const CustomLogin = props => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const { email, password } = formData;
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const loginHandler = async e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    ///TO-DO
    try {
      console.log('CLICK ON LOGIN SEND' + email + password);

      props.loginUser({ email, password });
    } catch (error) {
      console.log(error);
      //alert
    }
  };

  return props.isAuth && !props.loading ? (
    <Redirect to='/' />
  ) : (
    <div>
      <section class='sign-in'>
        <div class='container'>
          <div class='signin-content'>
            <div class='signin-image'>
              <figure>
                <img src='images/signin-image.jpg' alt='sing up image' />
              </figure>
              <a href='#' class='signup-image-link'>
                Create an account
              </a>
            </div>

            <div class='signin-form'>
              <h2 class='form-title'>Sign up</h2>
              <form class='register-form' id='login-form'>
                <div class='form-group'>
                  <label for='your_name'>
                    <i class='zmdi zmdi-account material-icons-name'></i>
                  </label>
                  <input
                    type='email'
                    placeholder='כתובת מייל'
                    name='email'
                    //onChange={e => onPasswordChange(e)}
                    required
                    onChange={e => onChange(e)}
                  />
                </div>
                <div class='form-group'>
                  <label for='your_pass'>
                    <i class='zmdi zmdi-lock'></i>
                  </label>
                  <input
                    bsSize='lg'
                    type='password'
                    placeholder='סיסמא'
                    name='password'
                    onChange={e => onChange(e)}
                  />
                </div>
                <div class='form-group'>
                  <input
                    type='checkbox'
                    name='remember-me'
                    id='remember-me'
                    class='agree-term'
                  />
                  <label for='remember-me' class='label-agree-term'>
                    <span>
                      <span></span>
                    </span>
                    Remember me
                  </label>
                </div>
                <div class='form-group form-button'>
                  <input
                    value='התחברות'
                    onClick={e => loginHandler(e)}
                    type='submit'
                    id='signin'
                    class='form-submit'
                  />
                </div>
              </form>
              <div class='social-login'>
                <span class='social-label'>Or login with</span>
                <ul class='socials'>
                  <li>
                    <a href='#'>
                      <i class='display-flex-center zmdi zmdi-facebook'></i>
                    </a>
                  </li>
                  <li>
                    <a href='#'>
                      <i class='display-flex-center zmdi zmdi-twitter'></i>
                    </a>
                  </li>
                  <li>
                    <a href='#'>
                      <i class='display-flex-center zmdi zmdi-google'></i>
                    </a>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};
const mapStateToProps = state => ({
  isAuth: state.auth.isFullAut,
  loading: state.auth.loading
});
export default connect(mapStateToProps, { loginUser })(CustomLogin);
