import { React, useState } from 'react';
import { loginUser } from '../../action/auth';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import {
  Container,
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText
} from 'reactstrap';
import './Login.css';
import '../../App.css';

const [formData, setFormData] = useState({
  email: '',
  password: ''
});

const { email, password } = formData;
const Login = props => {
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const loginHandler = async e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    ///TO-DO
    try {
      props.loginUser({ email, password });
    } catch (error) {
      console.log(error);
      //alert
    }

    ///if user already exist
    ///TO-DO
  };
  return !this.props.token ? (
    <Redirect to='/' />
  ) : (
    <Container fluid>
      <div dir='rtl' className='cont'>
        <Row>
          <Col md='12'>
            <div className='loginBox'>
              <section dir='rtl'>
                <Col md='12'>
                  <h1 className='  text-center'>התחברות</h1>
                  <br />
                  <hr />
                  <p
                    style={{ float: 'right' }}
                    className='lead text-center'
                    dir='rtl'
                  >
                    <i className='fas fa-user'> </i> על מנת להתחבר הזן כתובת
                    מייל וסיסמא
                  </p>
                  <Form dir='rtl' action='dashboard.html'>
                    <FormGroup>
                      <br />
                      <br />
                      <br />
                      <Label style={{ float: 'right' }} dir='rtl' dir='rtl'>
                        כתובת מייל
                      </Label>
                      <Input
                        bsSize='lg'
                        type='password'
                        placeholder='כתובת מייל'
                        name='password'
                        //onChange={e => onPasswordChange(e)}
                        required
                        onChange={e => onChange(e)}
                      />
                    </FormGroup>
                    <FormGroup>
                      <Label style={{ float: 'right' }}>סיסמא</Label>
                      <Input
                        bsSize='lg'
                        type='password'
                        placeholder='סיסמא'
                        name='Password'
                        onChange={e => onChange(e)}
                        onSubmit={e => loginHandler(e)}
                      />
                    </FormGroup>
                    <Input
                      bsSize='lg'
                      type='submit'
                      //onClick={e => passwordHandler(e)}
                      className='btn btn-primary'
                      value='התחברות'
                    />
                  </Form>
                  <p style={{ float: 'right' }} className='my-1'>
                    אין לך משתמש? <a href='/'>רישום</a>
                  </p>
                </Col>
              </section>
            </div>
          </Col>
        </Row>
      </div>
    </Container>
  );
};
const mapStateToProps = state => ({
  login: state.auth
});

export default connect({ loginUser })(Login);
