import { SET_MODAL, REMOVE_MODAL } from './types';
import uuid from 'uuid';
export const setModal = (msg, alertType) => dispatch => {
  const id = uuid.v4();
  dispatch({
    type: SET_ALERT,
    payload: { msg, alertType, id }
  });
};
