import React, { Component } from 'react';
import { connect } from 'react-redux';
import { loadSurveyToFill } from '../../../action/survey';
import Loading from '../../layout/Loading/Loading';
import { Form, FormGroup, Label, Input, FormText, Col, Row } from 'reactstrap';

class FillSurvey extends Component {
  state = {
    survey: []
  };
  componentDidMount = async () => {
    let som = 'MySeker';
    const title = 'MySeker';
    console.log(title);

    await this.props.loadSurveyToFill(title);
    this.setState({
      survey: [...this.props.survey]
    });
    console.log('=================================');

    console.log(this.state.survey);
    const ww = this.props.survey.map(element => {
      return element.isChoosenAnswer
        ? element.answers.map(ans => {
            return ans.answer;
          })
        : null;
    });
    console.log(ww);
  };

  render() {
    const s = this.props.survey;
    const customForm = this.props.loading ? (
      <Loading />
    ) : (
      <Form>
        {this.state.survey.map(element => {
          return element.isChoosenAnswer ? (
            <FormGroup style={{ height: '100%' }} tag='fieldset'>
              <legend>{`  ${element.qust}`}</legend>

              {element.answers.map(ans => {
                return (
                  <FormGroup style={{ height: '100%' }} check>
                    <Label htmlFor={ans.answer} check>
                      {' '}
                      {`ANSWER: ${ans.answer}`}
                      {''}
                    </Label>
                    <Input type='radio' value={ans.answer} name={ans.answer} />
                    <br />
                  </FormGroup>
                );
              })}
            </FormGroup>
          ) : (
            <Loading></Loading>
          );
        })}
      </Form>
    );

    return (
      <div>
        {customForm}

        {/* <Form>{customForm}</Form> */}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  survey: state.survey.survey,
  isLoading: state.survey.loading
});

export default connect(mapStateToProps, { loadSurveyToFill })(FillSurvey);
