import React, { Fragment } from 'react';
import { FormGroup, Container, Form } from 'reactstrap';
import { connect } from 'react-redux';
import { setAlert } from '../../action/alert';
import { setModal } from '../../action/modal';

import '../../App.css';

const Register = props => {
  const RegisterHandler = e => {
    e.preventDefault();
    ///if some of the input is wrong we alert to user
    props.setAlert('confirm', 'danger'); ///To do the spesidic alert

    props.setModal('assadsadasdasd', 'as');
  };

  return (
    <Fragment>
      <Container fluid>
        <section class='container'>
          <h1 class='large text-primary'>רישום</h1>
          <p class='lead'>
            <i class='fas fa-user'></i> צור את השתמש שלך
          </p>
          <Form dir='rtl' class='form' action='create-profile.html'>
            <FormGroup>
              <input type='text' placeholder='שם' name='name' required />
            </FormGroup>
            <FormGroup>
              <input type='מייל' placeholder='כתובת מייל @' name='email' />
              <small class='form-text'>
                This site uses Gravatar so if you want a profile image, use a
                Gravatar email
              </small>
            </FormGroup>
            <FormGroup>
              <input
                type='text'
                placeholder='טלפון'
                name='phone'
                minLength='6'
              />
            </FormGroup>
            <FormGroup>
              <input
                type='text'
                placeholder='כתובת'
                name='adress'
                minLength='6'
              />
            </FormGroup>
            <input
              onClick={e => RegisterHandler(e)}
              type='button'
              class='btn btn-primary'
              value='הרשם'
            />
          </Form>
          <p class='my-1'>
            Already have an account? <a href='login.html'>Sign In</a>
          </p>
        </section>
      </Container>
    </Fragment>
  );
};

export default connect(null, { setAlert, setModal })(Register);
