import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { loadSurveyToFill } from '../../../action/survey';
import Loading from '../../layout/Loading/Loading';
import {
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Col,
  Row,
  Container
} from 'reactstrap';

class FillSurvey extends Component {
  state = {
    survey: []
  };

  componentDidMount = async () => {
    let { surveyN } = this.state.survey;

    let som = 'MySeker';
    const title = 'MM';
    console.log(title);

    await this.props.loadSurveyToFill(title);
    this.setState({
      survey: [...this.props.survey]
    });
    console.log('=================================');

    console.log(this.state.survey);
    const ww = this.props.survey.map(element => {
      return element.isChoosenAnswer
        ? element.answers.map(ans => {
            return ans.answer;
          })
        : null;
    });
    console.log(ww);
  };
  onChengeHandler = e => {
    [e.target.name] = e.target.value;
    console.log(e.target);
  };
  onChengeTextHandler = e => {
    console.log([e.target.name]);
    console.log(e.target);
    console.log(e.target.value);
    console.log(this.state);

    this.setState({
      [e.target.name]: e.target.value
    });
    console.log(this.state.survey.find(q => q == e.target.name));

    // let surveyN=this.state.survey;
    // surveyN.qut=this.state.survey.qust
    // this.setState({
    //   survey
    // })
  };
  render() {
    const s = this.props.survey;
    const customForm = this.props.loading ? (
      <Loading />
    ) : (
      <Fragment>
        <h1>{this.state.survey.title}</h1>
        <Form>
          {this.state.survey.map(element => {
            return element.isChoosenAnswer ? (
              <FormGroup style={{ height: '100%' }} tag='fieldset'>
                <legend>{`  ${element.qust}`}</legend>
                <ul>
                  {element.answers.map(ans => {
                    return (
                      <Container>
                        <li>
                          <FormGroup check>
                            <Label
                              check
                              style={{ width: '20rem', marginLeft: '0.3rem' }}
                            >
                              <Input
                                style={{ width: '1rem' }}
                                type='radio'
                                value={element.qust}
                                name={element.qust}
                                onChange={e => this.onChengeHandler(e)}
                              />
                              {'   '}
                              {`ANSWER: ${ans.answer}`}
                            </Label>
                            <br />
                          </FormGroup>
                        </li>
                      </Container>
                    );
                  })}
                </ul>
              </FormGroup>
            ) : (
              <FormGroup style={{ height: '100%' }} tag='fieldset'>
                <legend>{`  ${element.qust}`}</legend>
                <ul>
                  {element.answers.map(ans => {
                    return (
                      <Container>
                        <li>
                          <FormGroup check>
                            <Label
                              check
                              style={{ width: '20rem', marginLeft: '0.3rem' }}
                            >
                              <Input
                                style={{ width: '14rem', marginTop: '1rem' }}
                                type='text'
                                name={element.qust}
                                value={ans.answer}
                                onChange={e => this.onChengeTextHandler(e)}
                              />
                              {'   '}
                              {`ANSWER: ${ans.answer}`}
                            </Label>
                            <br />
                          </FormGroup>
                        </li>
                      </Container>
                    );
                  })}
                </ul>
              </FormGroup>
            );
          })}
          <button className='btn-info'>SAND</button>
        </Form>
      </Fragment>
    );

    return (
      <div>
        {customForm}

        {/* <Form>{customForm}</Form> */}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  survey: state.survey.survey,
  isLoading: state.survey.loading
});

export default connect(mapStateToProps, { loadSurveyToFill })(FillSurvey);
