import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { loadSurveyToFill, fillSurveyCom } from '../../../action/survey';
import Loading from '../../layout/Loading/Loading';
import {
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Col,
  Row,
  Container,
  Button
} from 'reactstrap';

class FillSurvey extends Component {
  state = {
    title: '',
    survey: []
  };

  componentDidMount = async () => {
    let { surveyN } = this.state.survey;

    let som = 'MySeker';
    const title = 'f';

    await this.props.loadSurveyToFill(title);
    console.log(this.props.survey);

    const surveyQuts = this.props.survey.map((el, index) => {
      console.log(el);

      return {
        qut: {
          qust: el.qust
        },
        index: index,
        answers: [...el.answers]
      };
    });

    console.log(surveyQuts);

    this.setState({
      title: title,
      survey: [...this.props.survey]
    });

    const ww = this.props.survey.map(element => {
      return element.isChoosenAnswer
        ? element.answers.map(ans => {
            return ans.answer;
          })
        : null;
    });
    console.log(ww);
  };
  onChengeHandler = e => {
    [e.target.name] = e.target.value;
    console.log(e.target);
  };
  onChengeTextHandler = e => {
    let name = e.target.name;
    console.log(this.state.survey);

    let index = this.state.survey.findIndex(x => x.qust == name);

    this.setState({
      ...(this.state.survey[index].answers[0].answer = e.target.value)
    });
  };

  SubmitTheSurvey = e => {
    e.preventDefault();
    console.log('Submit Surviy');
    this.props.fillSurveyCom('tt', this.state.survey);
  };
  render() {
    const s = this.props.survey;
    const customForm = this.props.loading ? (
      <Loading></Loading>
    ) : (
      <Fragment>
        <h1>{this.state.survey.title}</h1>
        <Form>
          {this.props.isLoading ? <Loading></Loading> : null}
          {this.state.survey.map(element => {
            return element.isChoosenAnswer ? (
              <FormGroup style={{ height: '100%' }} tag='fieldset'>
                <legend>{`  ${element.qust}`}</legend>
                <ul>
                  {element.answers.map(ans => {
                    return (
                      <Container>
                        <li>
                          <FormGroup check>
                            <Label
                              check
                              style={{ width: '20rem', marginLeft: '0.3rem' }}
                            >
                              <Input
                                style={{ width: '1rem' }}
                                type='radio'
                                value={element.qust}
                                name={element.qust}
                                onChange={e => this.onChengeHandler(e)}
                              />
                              {'   '}
                              {`ANSWER: ${ans.answer}`}
                            </Label>
                            <br />
                          </FormGroup>
                        </li>
                      </Container>
                    );
                  })}
                </ul>
              </FormGroup>
            ) : (
              <FormGroup style={{ height: '100%' }} tag='fieldset'>
                <legend>{`  ${element.qust}`}</legend>
                <ul>
                  {element.answers.map(ans => {
                    return (
                      <Container>
                        <li>
                          <FormGroup check>
                            <Label
                              check
                              style={{ width: '20rem', marginLeft: '0.3rem' }}
                            >
                              <Input
                                style={{ width: '14rem', marginTop: '1rem' }}
                                type='text'
                                name={element.qust}
                                value={ans.answer}
                                onChange={e => this.onChengeTextHandler(e)}
                              />
                              {'   '}
                              {`ANSWER: ${ans.answer}`}
                            </Label>
                            <br />
                          </FormGroup>
                        </li>
                      </Container>
                    );
                  })}
                </ul>
              </FormGroup>
            );
          })}
          <Button onClick={e => this.SubmitTheSurvey(e)} color='primary'>
            שלח
          </Button>{' '}
        </Form>
      </Fragment>
    );

    return <div>{this.props.loading ? <Loading></Loading> : customForm}</div>;
  }
}

const mapStateToProps = state => ({
  survey: state.survey.survey,
  isLoading: state.survey.loading
});

export default connect(mapStateToProps, { loadSurveyToFill, fillSurveyCom })(
  FillSurvey
);
