import { GET_PROFILE, PROFILE_ERORR } from '../action/types';

const intialState = {
  profile: null,
  allProfile: [],
  sekers: [],
  loading: true,
  error: {}
};

export const profile = (state = intialState, action) => {
  const { type, payload } = action;

  switch (type) {
    case GET_PROFILE:
      return {
        ...state,
        profile: payload.profile,
        loading: false
      };
    case PROFILE_ERORR:
      return {
        ...state,
        profile: null,
        loading: false,
        error: payload.error
      };
  }
};
