import { GET_SURVEY, SURVEY_ERROR, FILL_SURVEY_SUCC } from './types';
import axios from 'axios';

export const loadSurvey = title => async dispatch => {
  try {
    const res = await axios.get('api/seker/:title');
    dispatch({
      type: GET_SURVEY,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: SURVEY_ERROR,
      payload: { msg: error.response.statusText, status: error.response.status }
    });
  }
};
