import React, { Component } from 'react';
import { Container } from 'reactstrap';
import '../../App.css';
export class PassworPage extends Component {
  state = {
    password: '',
    confirmPassword: ''
  };

  render() {
    const { password, confirmPassword } = this.state;

    const onPasswordChange = e => {
      this.setState({ password: e.target.value });
    };
    const onConfirmPasswordChange = e => {
      this.setState({ confirmPassword: e.target.value });
    };
    const passwordHandler = e => {
      e.preventDefault();
      if (password !== confirmPassword) {
        console.log('not match');

        ///ToDO Alert
      } else {
        ///Todo Alert if the password is iligal
        console.log('mach');
      }
    };
    return (
      <div>
        <Container>
          <section dir='rtl' class='container'>
            <h1 class='large text-primary'>סיום הרשמה</h1>
            <p class='lead text-center' dir='rtl'>
              <i class='fas fa-user'></i> בחר סיסמא בעלת 6 תווים או יותר
            </p>
            <form dir='rtl' class='form' action='dashboard.html'>
              <div class='form-group'>
                <input
                  type='password'
                  placeholder='Email Address'
                  name='password'
                  onChange={e => onPasswordChange(e)}
                  required
                />
              </div>
              <div class='form-group'>
                <input
                  type='password'
                  placeholder='Password'
                  name='confirmPassword'
                  onChange={e => onConfirmPasswordChange(e)}
                />
              </div>
              <input
                type='submit'
                onClick={e => passwordHandler(e)}
                class='btn btn-primary'
                value='Login'
              />
            </form>
            <p class='my-1'>
              Don't have an account? <a href='register.html'>Sign Up</a>
            </p>
          </section>
        </Container>
      </div>
    );
  }
}

export default PassworPage;
